package metodosdeformato;

public class MetodosParte1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// String Clase
		
		String mensaje = "     Hola, soy un String    ";
		
		System.out.println(mensaje.toUpperCase().trim()); // mayusculas
		System.out.println(mensaje.toLowerCase().trim()); // minusculas
		
		System.out.println(mensaje.trim()); // reducir espacios
		
		System.out.println(mensaje);

		
	}

}
