package switchcase;

public class Switch2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// Switch
		char calificacion = 'D';
		
		switch(calificacion)
		{
		case 'A':
			System.out.println("Excelente!");
			break;
		case 'B':
			System.out.println("Bien hecho");
			break;
		case 'C':
			System.out.println("Bien hecho");
			break;
		case 'D':
			System.out.println("Aprobatorio");
			break;
		}

	}

}
